import React, { useState, useRef } from 'react';
import { Upload, Play, Clock, Image, FileVideo, CheckCircle, Loader2 } from 'lucide-react';

const VideoMemeAnalyzer = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [results, setResults] = useState(null);
  const [videoUrl, setVideoUrl] = useState(null);
  const videoRef = useRef(null);

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('video/')) {
      setSelectedFile(file);
      setVideoUrl(URL.createObjectURL(file));
      setResults(null);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setIsUploading(true);
    const formData = new FormData();
    formData.append('video', selectedFile);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        setResults(data);
      } else {
        alert('Upload failed. Please try again.');
      }
    } catch (error) {
      console.error('Upload error:', error);
      alert('Upload failed. Please check if the backend server is running.');
    } finally {
      setIsUploading(false);
    }
  };

  const jumpToTimestamp = (timestamp) => {
    if (videoRef.current) {
      videoRef.current.currentTime = timestamp;
      videoRef.current.play();
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold mb-8 text-center">
          AI Video Meme Analyzer
        </h1>

        {/* Upload Section */}
        <div className="bg-gray-800 rounded-lg p-6 mb-8">
          <div className="flex items-center justify-center w-full mb-4">
            <label className="flex flex-col items-center justify-center w-full h-64 border-2 border-gray-600 border-dashed rounded-lg cursor-pointer bg-gray-700 hover:bg-gray-600 transition-colors">
              <div className="flex flex-col items-center justify-center pt-5 pb-6">
                <Upload className="w-10 h-10 mb-3 text-gray-400" />
                <p className="mb-2 text-sm text-gray-400">
                  <span className="font-semibold">Click to upload</span> or drag and drop
                </p>
                <p className="text-xs text-gray-400">MP4, AVI, MOV files supported</p>
              </div>
              <input
                type="file"
                className="hidden"
                accept="video/*"
                onChange={handleFileSelect}
              />
            </label>
          </div>

          {selectedFile && (
            <div className="flex items-center justify-between bg-gray-700 p-4 rounded-lg mb-4">
              <div className="flex items-center">
                <FileVideo className="w-5 h-5 mr-2 text-blue-400" />
                <span className="text-sm">{selectedFile.name}</span>
              </div>
              <button
                onClick={handleUpload}
                disabled={isUploading}
                className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-600 px-6 py-2 rounded-lg flex items-center transition-colors"
              >
                {isUploading ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Processing...
                  </>
                ) : (
                  <>
                    <Upload className="w-4 h-4 mr-2" />
                    Analyze Video
                  </>
                )}
              </button>
            </div>
          )}
        </div>

        {/* Processing Complete */}
        {results && (
          <div className="bg-green-800 bg-opacity-50 border border-green-600 rounded-lg p-4 mb-8">
            <div className="flex items-center">
              <CheckCircle className="w-5 h-5 mr-2 text-green-400" />
              <span className="text-green-200">Processing completed! Found {results.suggestions.length} meme suggestions.</span>
            </div>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Video Player */}
          <div className="lg:col-span-2">
            {videoUrl && (
              <div className="bg-gray-800 rounded-lg p-4">
                <h2 className="text-xl font-semibold mb-4 flex items-center">
                  <Play className="w-5 h-5 mr-2 text-blue-400" />
                  Video Player
                </h2>
                <video
                  ref={videoRef}
                  src={videoUrl}
                  controls
                  className="w-full h-auto max-h-96 bg-black rounded-lg"
                >
                  Your browser does not support video playback.
                </video>
              </div>
            )}

            {/* AI Suggestions Timeline */}
            {results && (
              <div className="bg-gray-800 rounded-lg p-4 mt-6">
                <h2 className="text-xl font-semibold mb-4 flex items-center">
                  <Clock className="w-5 h-5 mr-2 text-green-400" />
                  AI Suggestions
                </h2>
                <div className="space-y-3">
                  {results.suggestions.map((suggestion, index) => (
                    <div
                      key={index}
                      onClick={() => jumpToTimestamp(suggestion.timestamp)}
                      className="bg-gray-700 hover:bg-gray-600 p-4 rounded-lg cursor-pointer transition-colors border-l-4 border-blue-500"
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div className="bg-blue-600 text-white px-2 py-1 rounded text-sm mr-3">
                            {formatTime(suggestion.timestamp)}-{formatTime(suggestion.end_timestamp)}
                          </div>
                          <div>
                            <div className="font-medium text-white">{suggestion.meme_file.replace('.jpg', ' Meme')}</div>
                            <div className="text-gray-400 text-sm">{suggestion.description}</div>
                          </div>
                        </div>
                        <div className="flex items-center text-gray-400">
                          <span className="text-xs mr-2">{Math.round(suggestion.confidence * 100)}% match</span>
                          <Image className="w-4 h-4" />
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Meme Library */}
          <div className="bg-gray-800 rounded-lg p-4">
            <h2 className="text-xl font-semibold mb-4 flex items-center">
              <Image className="w-5 h-5 mr-2 text-purple-400" />
              Meme Library
            </h2>
            <div className="space-y-4">
              {[
                { name: "Success Kid", type: "happy", desc: "Excited reaction" },
                { name: "Sad Cat", type: "sad", desc: "Disappointed reaction" },
                { name: "Angry Baby", type: "angry", desc: "Frustrated reaction" },
                { name: "Happy Dog", type: "happy", desc: "Joyful reaction" },
                { name: "Confused Cat", type: "confused", desc: "Puzzled reaction" },
                { name: "Satisfied Frog", type: "content", desc: "Content reaction" }
              ].map((meme, index) => (
                <div key={index} className="bg-gray-700 p-3 rounded-lg">
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="font-medium text-white">{meme.name}</div>
                      <div className="text-gray-400 text-sm">{meme.desc}</div>
                    </div>
                    <div className={`px-2 py-1 rounded text-xs ${
                      meme.type === 'happy' ? 'bg-green-600 text-green-100' :
                      meme.type === 'sad' ? 'bg-red-600 text-red-100' :
                      meme.type === 'angry' ? 'bg-orange-600 text-orange-100' :
                      'bg-blue-600 text-blue-100'
                    }`}>
                      {meme.type}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {results && (
              <div className="mt-6 p-3 bg-gray-700 rounded-lg">
                <h3 className="font-semibold text-green-400 mb-2">Analysis Complete</h3>
                <div className="text-sm text-gray-300">
                  <div>File: {results.video_file}</div>
                  <div>Suggestions: {results.suggestions.length}</div>
                  <div>Processed: {new Date(results.processed_at).toLocaleTimeString()}</div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoMemeAnalyzer;
